import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import { format, startOfMonth, addDays } from 'date-fns';

const EventCalen = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [events, setEvents] = useState(JSON.parse(localStorage.getItem('events')) || []);
  const [newEvent, setNewEvent] = useState('');

  useEffect(() => {
    localStorage.setItem('events', JSON.stringify(events));
  }, [events]);

  const addEvent = () => {
    if (!newEvent.trim()) return;
    setEvents([...events, { date: selectedDate.toISOString().split('T')[0], title: newEvent }]);
    setNewEvent('');
    setSelectedDate(null);
  };

  const clearEvents = () => {
    setEvents([]);
    localStorage.removeItem('events');
  };

  const daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  return (
    <div>
      <DatePicker
        selected={selectedDate}
        onChange={(date) => setSelectedDate(date)}
        dateFormat="MMMM d, yyyy"
      />
      <input
        type="text"
        value={newEvent}
        onChange={(e) => setNewEvent(e.target.value)}
        placeholder="Add event..."
      />
      <button onClick={addEvent}>Add Event</button>
      <button onClick={clearEvents}>Clear Events</button>
      <table>
        <thead>
          <tr>
            {daysOfWeek.map((day) => (
              <th key={day}>{day}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {Array.from({ length: 7 }).map((_, i) => {
            const day = startOfMonth(new Date()).addDays(i);
            return (
              <tr key={i}>
                <td>{format(day, 'iiii')}</td>
                {daysOfWeek.map((_, j) => (
                  <td key={`${i}-${j}`}>
                    {events.find(event => event.date.split('-').slice(0, 2).join('-') === day.toISOString().split('T')[0])?.title || '-'}
                  </td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default EventCalen;
